  const db = getDatabase()

  const encrypted = await encryptObject(memory, key, salt)
  await db.dailyMemories.put({
    id: memory.id,
    date: memory.date,
    coveringTo: memory.coveringTo,
    data: encrypted,
    createdAt: memory.createdAt
  })
}

